import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from 'styled-components';

const Btn = styled.button`
  background : ${props => props.bg};
  color : black;
  padding : 10px;
`
const Detail = (props) => {
  const { id } = useParams();
  const findShoes = props.shoes.find((item) => {
    return item.id == id;
  });
  const [showAlert, setShowAlert] = useState(true);
  useEffect(() => {
    setTimeout(() => {
      setShowAlert(false);
    }, 2000);
  })
  return (
    <div className="container">
      {showAlert ? <div className="alert alert-warning">2초 이내 구매시 할인</div> : null}
      <Btn bg="blue">노랑버튼</Btn>
      <div className="row">
        {id > props.shoes.length - 1 || isNaN(id) ? (
          <h1>상품없음</h1>
        ) : (
          <>
            <div className="col-md-6">
              <img
                src={'https://codingapple1.github.io/shop/shoes' + (findShoes.id + 1) + '.jpg'}
                width="100%"
              />
            </div>
            <div className="col-md-6">
              <h4 className="pt-5">{findShoes.title}</h4>
              <p>{findShoes.content}</p>
              <p>{findShoes.price}</p>
              <button className="btn btn-danger">주문하기</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Detail;
