import { useState } from "react";
import "./App.css";

function App() {
  let posts = "강남 우동 맛집";
  const [title, setTitle] = useState([
    "남자 코트 추천",
    "남자 상의 추천",
    "남자 바지 추천",
  ]);
  const [like, setLike] = useState(0);

  function onClickLike(){
    console.log('hi')
  }
  return (
    <div className="App">
      <div className="black-nav">
        <h2>{posts}</h2>
      </div>
      <div className="list">
        <button onClick={()=> setTitle([
    "여자 코트 추천",
    "남자 상의 추천",
    "남자 바지 추천",
  ])}></button>
        <h4>{title[0]}<span onClick={() => {setLike(like + 1)}}>❤️</span> {like}</h4>
        <p>2월 17일 발행</p>
      </div>
      <div className="list">
        <h4>{title[1]}</h4>
        <p>2월 17일 발행</p>
      </div>
      <div className="list">
        <h4>{title[2]}</h4>
        <p>2월 17일 발행</p>
      </div>
    </div>
  );
}

export default App;
