import { useState } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import data from "./data";
import { Navbar, Container, Nav } from "react-bootstrap";
import { Routes, Route, Link, useNavigate, Outlet } from 'react-router-dom';

import Detail from './pages/Detail';

function App() {
  const [shoes] = useState(data);
  const navigate = useNavigate();
  return (
    <>
        <Navbar bg="dark" variant="dark">
          <Container>
            <Navbar.Brand onClick={()=>{navigate('/')}}>
              <img src={process.env.PUBLIC_URL + "/logo.png"} width="100px" />
            </Navbar.Brand>
            <Nav className="me-auto">
              <Nav.Link onClick={()=>{navigate('/detail')}}>Detail</Nav.Link>
              <Nav.Link href="#pricing">Pricing</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
        <Link to="/">홈</Link>    
      <Routes>
        <Route path="/" element={
          <>
            <div className="main-bg"></div>
            <div className="container">
              <div className="row">
                {shoes.map((item, i) => <Card shoes={shoes[i]} i={i} key={item.id}/>)}
              </div>
            </div>
          </>
        }/>
        <Route path="/detail/:id" element={<Detail shoes={shoes}/>} />
        <Route path="/about" element={<About/>}>
          <Route path="member" element={<h2>멤버</h2>} />
          <Route path="location" element={<h2>위치</h2>} />
        </Route>
        <Route path="*" element={<h1>없는 페이지</h1>} />
      </Routes>
    </>
  );
}
function About(){
  return(
    <>
      <h1>어바웃</h1>
      <Outlet></Outlet>
    </>
  )
}

function Card(props) {
  return (
    <div className="col-md-4">
      <img src={`https://codingapple1.github.io/shop/shoes${props.i+1}.jpg`} width="80%" />
      <h4>{props.shoes.title}</h4>
      <p>{props.shoes.content}</p>
      <p>{props.shoes.price}</p>
    </div>
  );
}

export default App;
