import { useState } from "react";
import "./App.css";

function App() {
  let posts = "강남 우동 맛집";
  const [title, setTitle] = useState([
    "다 남자 코트 추천",
    "나 남자 상의 추천",
    "가 남자 바지 추천",
  ]);
  const [like, setLike] = useState(0);
  const [modal, setModal] = useState(false);
  return (
    <>
      <div className="black-nav">
        <h2>{posts}</h2>
      </div>
      <button onClick={()=> {
          // let copy = title.slice();
          let copy = [...title];
          copy[0] = '여자코트추천';
          setTitle(copy);
          }}>글 수정</button>
      <button onClick={()=> {
        const sortTitle = [...title].sort();
        setTitle(sortTitle);
      }}>가나다</button>

      {title.map((item, i)=> {
        return(
          <div className="list" key={item}>
            <h4 onClick={()=>{setModal(!modal)}}>{title[i]}</h4>
            <p>2월 17일 발행</p>
          </div>
        )
      })}
      {modal ? <Modal/> : null}
    </>
  );
}

function Modal(){
  return (
    <div className="modal">
        <h4>제목</h4>
        <p>날짜</p>
        <p>상세내용</p>
    </div>
  )
}

export default App;
