import { useState } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import data from "./data";
import { Routes, Route, Link, useNavigate, Outlet } from 'react-router-dom';
// pages
import Main from './pages/Main';
import Detail from './pages/Detail';
import Cart from './pages/Cart';
// components
import TopNav from "./components/TopNav";

function App() {
  const [shoes, setShoes] = useState(data);
  const [sortBtn, setSortBtn] = useState("low");
  const [more, setMore] = useState(2);
  return (
    <>
      <TopNav/>
      <Link to="/">홈</Link>    
      <Routes>
        <Route path="/" element={<Main shoes={shoes} setShoes={setShoes} sortBtn={sortBtn} setSortBtn={setSortBtn} more={more} setMore={setMore}/>}/>
        <Route path="/detail/:id" element={<Detail shoes={shoes}/>} />
        <Route path="/cart" element={<Cart />}/>
        <Route path="/about" element={<About/>}>
          <Route path="member" element={<h2>멤버</h2>} />
          <Route path="location" element={<h2>위치</h2>} />
        </Route>
        <Route path="*" element={<h1>없는 페이지</h1>} />
      </Routes>
    </>
  );
}

function About(){
  return(
    <>
      <h1>어바웃</h1>
      <Outlet></Outlet>
    </>
  )
}

export default App;
